export interface TxBatchDto {
  txs: TxRequestDto[];
  gasPrice?: string;
  nonce?: number;
  appSig?: string[];
}

export interface TxRequestDto {
  from: string;
  to: string;
  value?: string;
  data?: string;
  gasLimit?: string;
  nonce?: number;
  userSig?: string[];
}

export interface TxBatchInterface {
  txs: TxRequestInterface[];
  gasPrice: string;
  nonce: number | null;
  appSig: string[];
  policy: string;
}

export interface TxRequestInterface {
  from: string;
  to: string;
  value: string;
  data: string;
  gasLimit: string;
  nonce: number | null;
  userSig: string[];
}
